import React from 'react';


const button = [
    {id:0, imgURL: "./img/smart.jpeg", clicked:false},
    {id:1, imgURL: "./img/spong.jpg", clicked:false},
    {id:2, imgURL: "./img/Distracted-boyfriend.jpg", clicked:false},
    {id:3, imgURL: "./img/tellMeMore.jpeg", clicked:false},
    {id:4, imgURL: "./img/dafuq2.jpeg", clicked:false},
    {id:5, imgURL: "./img/dafuq.jpeg", clicked:false},
    {id:6, imgURL: "./img/cat.jpg", clicked:false},
    {id:7, imgURL: "./img/That-Would_Be-Great.jpg", clicked:false},
    {id:8, imgURL: "./img/mmm.jpeg", clicked:false},
    {id:9, imgURL: "./img/Evil-Kermit.jpg", clicked:false},
];

class Button extends React.Component {

    state = {
        // imgURL:"./img/mmm.jpeg",
        array: button,
        counter: "./img/mmm.jpeg"};



    handleClick = () => {
        this.setState({
            counter:this.state.counter + 1
        })

        if (this.state.counter >= 1) {
            console.log ("it works")
          }

    };





    render() {
        return(
            <button onClick={this.handleClick}>
                {this.state.counter}

            </button>
        );
    }
}

export default Button;
